const fse = require('fs-extra');

const dir = process.argv[2];

/* eslint-disable no-console */
fse.ensureDir(dir)
    .then(() => {
        console.log(`\n* mkdir.js: Successfully created './${dir}/' directory.\n`);
    })
    .catch((err) => {
        console.error(err);
    });
/* eslint-enable no-console */
