import Cookies from 'js-cookie';

class Cookie {
    static get(name) {
        return Cookies.get(name);
    }
    static getJson(name) {
        return Cookies.getJSON(name);
    }
    static getKey(name, key, delimiter = '&') {
        const cookieValue = Cookie.get(name);
        const expression = new RegExp(`${key}=([^${delimiter}]*)`);

        if (!cookieValue) {
            return undefined;
        }

        const match = cookieValue.match(expression);

        if (!match) {
            return undefined;
        }

        return match[1];
    }
}

export default Cookie;
