import cookie from './cookie';

const cookieName = 'KmxVisitor_0';

export default class KmxVisitorCookie {
    static get id() {
        return cookie.getKey(cookieName, 'VisitorID');
    }
    static get isFirstVisit() {
        return (cookie.getKey(cookieName, 'IsFirstVisit') || '').toLowerCase() === 'true';
    }
    static get isUsingStoreProxy() {
        return (cookie.getKey(cookieName, 'UsingStoreProxy') || '').toLowerCase() === 'true';
    }
    static get storeId() {
        return cookie.getKey(cookieName, 'StoreId');
    }
    static get userZip() {
        return cookie.getKey(cookieName, 'UserZip');
    }
    static get zip() {
        return cookie.getKey(cookieName, 'Zip');
    }
    static get isZipConfirmed() {
        return (cookie.getKey(cookieName, 'ZipConfirmed') || '').toLowerCase() === 'true';
    }
}
